
public class VarEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20, b=10, c=0;
		c=a+b;
		System.out.println(c);
		c=a-b;
		System.out.println(c);
		c=a*b;
		System.out.println(c);
		c=a/b;
		System.out.println(c);
		c=a%b;
		System.out.println(c);
		c=12%5;
		System.out.println(c);
		
		c=12%10;
		System.out.println(c);
		c=15%10;
		System.out.println(c);
		c=23%10;
		System.out.println(c);
		
		int i=5, j=5;
		System.out.println(i++);
		System.out.println(j--);
		System.out.println("i="+i+",j="+j);
	}

}
