package example;

public class Op2_00 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=5, j;
		j=i++;
		System.out.println("j="+j);
		j=++i;
		System.out.println("j="+j);
		j=i--;
		System.out.println("j="+j);
		j=--i;
		System.out.println();
		System.out.println();
		System.out.println();
		System.out.println();
		System.out.println();
		System.out.println();
		
		byte a = 10;
		byte b = 20;
		byte c =(byte)(a + b); // byte+byte -> int+int=int
		System.out.println(c);
		
		
	}

}
