
public class switchbreak_0 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a;
		  int b = 0;
		  for(a=1; a <=100; a+=2)
		  {
			  System.out.print(a + " ");
		   
			  b++;
			  if(b%5==0)    //5로나누어 떨어지면 개행
		   {
				  System.out.println();
		   }
	}
	}
}
